mapper.select:
select rownum rnum, Q.* from ( select db.* , c.categoryName AS categoryName , s.statusName AS statusName , COALESCE(dlt.countLike, 0) AS countLike , COALESCE(cr.chatRoomCount, 0) AS chatRoomCount from daangn_board db JOIN daangn_category c ON db.categoryRef = c.categoryIdx JOIN daangn_status s ON db.statusRef = s.statusIdx LEFT JOIN ( SELECT boardIdx, COUNT(*) AS countLike FROM daangn_like_tb GROUP BY boardIdx ) dlt ON dlt.boardIdx = db.idx LEFT JOIN ( SELECT boardIdx, COUNT(*) AS chatRoomCount FROM chatRoom GROUP BY boardIdx ) cr ON cr.boardIdx = db.idx where
db.idx < #{lastItemIdx}
and db.userRef = #{userRef}
and db.statusRef = #{statusRef}
AND db.statusRef <> 3
and db.categoryRef = #{categoryRef}
AND ( db.SUBJECT LIKE '%' || #{search} || '%' OR db.CONTENT LIKE '%' || #{search} || '%' )
AND db.LOC LIKE '%' || #{region} || '%'
AND db.LOC LIKE '%' || #{gu} || '%'
AND db.LOC LIKE '%' || #{dong} || '%'
AND db.idx <> #{boardRef}
order by db.idx DESC ) Q where
rownum <= #{sizeOfPage}
