mapper.select:
select R.* from ( select rownum rnum, Q.* from ( select * from notices where highlight = 0 order by idx ) Q where
rownum <= #{endNo}
) R where
rnum >= #{startNo}

mapper.insert:

			select notices_idx_seq.nextval from dual

    	insert into
    		notices
    	values
    		(
    			#{idx, jdbcType=INTEGER}
    			, #{title}
    			, #{content}
    			, sysdate
    			, 0
    		)

mapper.update:
UPDATE
	notices
SET

		title = #{title}, content = #{content}


		highlight = #{highlight}	

where idx = #{idx}
