mapper.insert:
insert into 
	daangn_member
values
	(
		daangn_member_idx_seq.nextval
		, #{username}
		, #{password}
		, #{role}
		, #{name}
		, #{nickName}
		, #{email}
		, #{emailOk}
		, sysdate
		, sysdate
		, #{stAddress}
		, #{dtAddress}
	)

mapper.select:
select R.* from ( select rownum rnum, Q.* from ( select * from daangn_member
username like '%' || #{search} || '%' or name like '%' || #{search} || '%' or nickName like '%' || #{search} || '%'
order by idx desc ) Q where
rownum <= #{endNo}
) R where
rnum >= #{startNo}

mapper.update:
update daangn_member
password = #{password}, role = #{role}, nickName = #{nickName}, email = #{email}, emailOk = #{emailOk}, stAddress = #{stAddress}, dtAddress = #{dtAddress},
where idx = #{idx}
